import React, {useState} from 'react'
import './App.css';
import {Main} from "./components/Main";
import {MainC} from "./components/MainC";



export const App = () => {
    const [count, setCount] = useState([])
    const [current, setCurrent] = useState(0)
    const add = (e) => {
        /*setCount(prev => {
            return [...prev, <Main current={current} setCurrent={setCurrent}/>]
        })*/
        setCount(prev => {
            return [...prev, 0]
        })
    }

    const plusNumber = (idx) => {
        let newArr = [...count]
        newArr[idx]++
        setCount(newArr)
    }


    const reset = () => {
        setCount([])
    }

    console.log(count)
  return (
    <div>
      <button onClick={add}>Add</button>
      <button onClick={reset}>Reset</button>
        {count.map((el,idx) => <MainC value={el} plusOne={()=>plusNumber(idx)}/>)}
    </div>
  );
}

export default App;
