import React, {useState} from 'react'
import './App.css';
import {Main} from "./components/Main";


let obj = {
    component: <Main/>,
    currentVal: 0
}

export const App = () => {
    const [count, setCount] = useState([])
    const [current, setCurrent] = useState(0)
    const add = (e) => {
        setCount(prev => {
            return [...prev, <Main current={current} setCurrent={setCurrent}/>]
        })
    }


    const reset = () => {
        setCount([])
    }
  return (
    <div>
      <button onClick={add}>Add</button>
      <button onClick={reset}>Reset</button>
        {count}
    </div>
  );
}

export default App;
